<?phpabstract class C_Base extends C_Controller{	protected $needLogin;	// свойство проверяющее необходимость автоматической авторизации в случае сохранения cookie	protected $user;		// текущий пользователь || null		public function getApiMethod($request){		switch ($request) {			case 'GET':				$this->getUser($_GET['id']);			break;			case 'POST':				if (isset($_POST['logout'])) {					$this->logoutUser();				}				else {					isset($_POST['UserEmail']) ? $this->addUser($_POST, $_FILES) : $this->loginUser($_POST);				}			break;			case 'PUT':				$this->editUser($_POST);			break;		}	}}